import { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import MovieList from './Liste';
import Filter from './Filter';
import AddMovie from './Addmov';
import './App.css'

const App = () => {
  const [movies, setMovies] = useState([
    {
      title: 'Inception',
      description: 'A thief steals corporate secrets through dream-sharing.',
      posterURL: 'https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcRRyuWmayVBvqjd1MxTKpRgauq2cCtUzb7Q9QvaFTkAuxAU_EYMoCE3wBuJeftxIzf0grreIw',
      rating: 8.8
    },
    {
      title: 'Interstellar',
      description: 'A team of explorers travel through a wormhole in space.',
      posterURL: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSngBJ0B7UDrLUkDlp6DCQLsEYuWR-DiHwbnxFFCniB3HiP3f3NZmR1-lKSC34ge6YXu4LX',
      rating: 8.6
    },
    {
      title: 'The Dark Knight',
      description: 'Batman faces the Joker, a criminal mastermind.',
      posterURL: 'data:image/jpeg;base64,',
      rating: 9.0 
    },
 
    {
      title: 'The Godfather',
      description: 'The aging patriarch of an organized crime dynasty transfers control to his reluctant son.',
      posterURL: 'data:image/jpeg;base64,',
      rating: 9.2
    }
  ]);

  const [filters, setFilters] = useState({ title: '', rating: 0 });

  const handleAddMovie = (movie) => {
    setMovies([...movies, { ...movie, rating: parseFloat(movie.rating) }]);
  };

  const handleFilterChange = (key, value) => {
    setFilters({ ...filters, [key]: value });
  };

  const filteredMovies = movies.filter(
    (movie) =>
      movie.title.toLowerCase().includes(filters.title.toLowerCase()) &&
      movie.rating >= parseFloat(filters.rating || 0)
  );

  return (
<div className="App">
  <h1>Jihed Flix</h1>
  <BrowserRouter>
    <Routes>
    <Route
      path="/filter"
      element={
        
        
        <details>
          <summary>Filter movies</summary>
          <Filter onFilter={handleFilterChange} />
        </details>
      }
    />
    <Route
      path="/add"
      element={
        <details>
          <summary>Add a movie</summary>
          <AddMovie onAddMovie={handleAddMovie} />
        </details>
      }
    />
    <Route path="/list" element={<MovieList movies={filteredMovies} />} />
    </Routes>

  </BrowserRouter>
  
</div>
  );
};

export default App;
